    <script>
        let stompClient = null;
        const filmId = 1; // Hardcoded for testing

        function log(message) {
            const output = document.getElementById('output');
            output.innerHTML += `<div>${new Date().toISOString()}: ${message}</div>`;
        }

        function connect() {
            log("Attempting connection...");
            const socket = new SockJS('/ex00_war/ws');
            stompClient = Stomp.over(socket);

            stompClient.debug = (msg) => log(`STOMP: ${msg}`);

            stompClient.connect({},
                (frame) => {
                    log(`Connected! Frame: ${frame.headers}`);
                    stompClient.subscribe(`/topic/films/${filmId}/chat/messages`, (message) => {
                        log(`Received: ${message.body}`);
                    });
                },
                (error) => {
                    log(`Connection error: ${error}`);
                }
            );
        }

        function sendTest() {
            if (!stompClient || !stompClient.connected) {
                log("Not connected!");
                return;
            }

            const testMessage = {
                sender: "debug-user",
                content: "TEST MESSAGE " + Date.now()
            };

            log(`Sending to /app/films/${filmId}/chat/send: ${JSON.stringify(testMessage)}`);
            stompClient.send(
                `/app/films/${filmId}/chat/send`,
                {},
                JSON.stringify(testMessage)
            );
        }

        function testTest() {
            stompClient.send("/app/test", {}, "Hello WebSocket!");
        }
    </script>
