   208	            logger.info("Successfully sent test message to chat topic");
   209	
   210	            // Return the echo message - Spring will automatically broadcast it to the destination
   211	            // specified in the @SendTo annotation
   212	            return echoMessage;
   213	        } catch (Exception e) {
   214	            logger.error("Error processing message: {}", e.getMessage(), e);
   215	            e.printStackTrace();
   216	            throw e; // Re-throw to let Spring handle the error
   217	        } finally {
   218	            logger.info("========== TEST MESSAGE PROCESSING COMPLETE ==========");
