    98	            } catch (IOException e) {
    99	                logger.warn("Could not convert string to ChatMessage, creating a default one: {}", e.getMessage());
   100	                // Create a default ChatMessage if conversion fails
   101	                chatMessage = new ChatMessage("system", messageString);
   102	            }
   103	
   104	            // Also send the message directly using the messaging template
   105	            // This is a backup in case the @SendTo annotation doesn't work
   106	            String destination = "/topic/films/" + filmId + "/chat/messages";
   107	            logger.info("Also sending message directly to {}", destination);
   108	            messagingTemplate.convertAndSend(destination, chatMessage);
   109	
