package fr._42.cinema.controller;

import fr._42.cinema.models.ChatMessage;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.messaging.handler.annotation.MessageMapping;
import org.springframework.messaging.handler.annotation.Payload;
import org.springframework.messaging.handler.annotation.SendTo;
import org.springframework.messaging.simp.SimpMessagingTemplate;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.ResponseBody;

@Controller
public class WebSocketDemoController {

    private final Logger logger = LoggerFactory.getLogger(WebSocketDemoController.class);
    private final SimpMessagingTemplate messagingTemplate;

    public WebSocketDemoController(SimpMessagingTemplate messagingTemplate) {
        this.messagingTemplate = messagingTemplate;
    }

    @GetMapping("/ws-demo")
    public String getWebSocketDemoPage() {
        return "ws-demo";
    }

    @GetMapping("/ws-broadcast")
    @ResponseBody
    public String broadcastMessage() {
        logger.info("Broadcasting test messages");

        try {
            // Send to test topic
            messagingTemplate.convertAndSend("/topic/test", "Test broadcast from controller");
            logger.info("Sent message to /topic/test");

            // Send to films topic
            ChatMessage chatMessage = new ChatMessage("system", "Test broadcast from controller");
            messagingTemplate.convertAndSend("/topic/films/1/chat/messages", chatMessage);
            logger.info("Sent message to /topic/films/1/chat/messages");

            return "Messages broadcast successfully. Check your WebSocket client.";
        } catch (Exception e) {
            logger.error("Error broadcasting messages: {}", e.getMessage(), e);
            return "Error: " + e.getMessage();
        }
    }

    @MessageMapping("/ws-direct")
    public void sendDirectMessage(@Payload ChatMessage chatMessage) {
        logger.info("Received direct message: {}", chatMessage);

        // Send directly to the topic
        String destination = "/topic/films/1/chat/messages";
        logger.info("Sending message directly to {}", destination);
        messagingTemplate.convertAndSend(destination, chatMessage);
        logger.info("Message sent directly to {}", destination);
    }

    @MessageMapping("/echo")
    @SendTo("/topic/echo")
    public String echo(String message) {
        logger.info("Received echo message: {}", message);
        return "Echo: " + message;
    }

    @MessageMapping("/hello")
    @SendTo("/topic/greetings")
    public String greeting(String message) {
        logger.info("Received hello message: {}", message);
        return "Hello, " + message + "!";
    }
