        function connect() {
            log('Connecting to WebSocket...', 'info');

            const socket = new SockJS('/ex00_war/ws');
            stompClient = Stomp.over(socket);

            // Enable debug logging
            stompClient.debug = function(str) {
                console.log(str);
            };

            stompClient.connect({}, function(frame) {
                log('Connected: ' + frame, 'success');
                setConnected(true);

                // Subscribe to film chat messages
                log('Subscribing to /topic/films/1/chat/messages', 'info');
                stompClient.subscribe('/topic/films/1/chat/messages', function(message) {
                    try {
                        const chatMessage = JSON.parse(message.body);
                        log('Received chat: ' + chatMessage.sender + ': ' + chatMessage.content, 'received');
                    } catch (e) {
                        log('Received raw chat: ' + message.body, 'received');
                    }
                });

                // Subscribe to echo topic
                log('Subscribing to /topic/echo', 'info');
                stompClient.subscribe('/topic/echo', function(message) {
                    log('Received echo: ' + message.body, 'received');
                });
...

        function sendDirectMessage() {
            const sender = document.getElementById('sender').value || 'anonymous';
            const content = document.getElementById('content').value || 'Test message ' + new Date().toISOString();

            const chatMessage = {
                sender: sender,
                content: content
            };

            log('Sending direct message: ' + JSON.stringify(chatMessage), 'sent');
            stompClient.send('/app/ws-direct', {'content-type': 'application/json'}, JSON.stringify(chatMessage));
            document.getElementById('content').value = '';
        }

