     9	import org.springframework.stereotype.Controller;
    10	import org.springframework.ui.Model;
    11	import org.springframework.web.bind.annotation.GetMapping;
    12	import org.springframework.web.bind.annotation.PostMapping;
    13	import org.springframework.web.bind.annotation.RequestMapping;
    14	import org.springframework.web.bind.annotation.RequestParam;
    15	import org.springframework.web.multipart.MultipartFile;
    16	
    17	import java.io.File;
    18	import java.io.IOException;
    19	import java.util.List;
    20	import java.util.UUID;
    21	
    22	@Controller
    23	@RequestMapping("/admin/panel/films")
    24	public class FilmsController {
    25	    private FilmsService filmsService;
    26	    final private Logger logger = LoggerFactory.getLogger(FilmsController.class);
    27	
    28	    @Value("${posterUpload.dir}")
    29	    String uploadDirS;
    30	
    31	    public FilmsController(FilmsService filmsService) {
    32	        this.filmsService = filmsService;
    33	    }
    34	
    35	    @GetMapping(value = {"", "/"})
    36	    public String getFilms(Model model) {
    37	        try {
    38	            List<Film> films = filmsService.getFilms();
    39	            model.addAttribute("films", films);
    40	            return "films";
    41	        } catch (Exception e) {
    42	            logger.info("error: " + e.getMessage());
    43	            return "films";
    44	        }
    45	    }
    46	
    47	    @PostMapping(value = {"", "/"})
    48	    public String postFilms(
    49	            @RequestParam("title") String title,
    50	            @RequestParam("year") Integer year,
    51	            @RequestParam("ageRestrictions") Integer ageRestrictions,
    52	            @RequestParam("description") String description,
    53	            @RequestParam("poster") MultipartFile poster,
    54	            Model model
    55	    )  {
    56	        if (title == null || title.trim().isEmpty()
    57	                || year == null || year == 0
    58	                || ageRestrictions == null
    59	                || description == null || description.trim().isEmpty()) {
    60	            model.addAttribute("error", "Please fill in all required fields.");
    61	            return "redirect:/admin/panel/films";
    62	        }
    63	        String posterUrl = null;
    64	        if (!poster.isEmpty()){
    65	            try {
    66	                File uploadDir = new File(uploadDirS);
    67	                if (!uploadDir.exists())
    68	                    uploadDir.mkdirs();
    69	                String originalFilename = poster.getOriginalFilename();
    70	                String extension = originalFilename.substring(originalFilename.lastIndexOf('.'));
    71	                String uniqueFileName = UUID.randomUUID() + extension;
    72	                File dest = new File(uploadDir, uniqueFileName);
    73	
    74	                poster.transferTo(dest);
    75	                posterUrl = uniqueFileName;
    76	            } catch (SecurityException | IOException e) {
    77	                model.addAttribute("error", "error while saving the poster");
    78	                return "redirect:/admin/panel/films";
    79	            }
    80	
    81	        }
