    10	import org.springframework.context.annotation.Bean;
    11	import org.springframework.context.annotation.ComponentScan;
    12	import org.springframework.context.annotation.Configuration;
    13	import org.springframework.context.annotation.Import;
    14	import org.springframework.data.jpa.repository.config.EnableJpaRepositories;
    15	import org.springframework.orm.jpa.JpaTransactionManager;
    16	import org.springframework.orm.jpa.LocalContainerEntityManagerFactoryBean;
    17	import org.springframework.orm.jpa.vendor.HibernateJpaVendorAdapter;
    18	import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
    19	import org.springframework.transaction.PlatformTransactionManager;
    20	
    21	import javax.sql.DataSource;
    22	import java.util.Properties;
    23	
    24	@Configuration
    25	@EnableJpaRepositories("fr._42.cinema.repositories")
    26	@ComponentScan(basePackages = "fr._42.cinema.services")
    27	@Import(PropertyConfig.class)
    28	public class AppConfig {
    29	
    30	    @Value("${db.url}")
    31	    private String dbUrl;
    32	
    33	    @Value("${db.username}")
    34	    private String dbUsername;
    35	
    36	    @Value("${db.password}")
    37	    private String dbPassword;
    38	
    39	    @Value("${db.driver}")
    40	    private String dbDriver;
    41	
    42	    @Bean
    43	    public DataSource dataSource() {
    44	
    45	        HikariConfig config = new HikariConfig();
    46	        config.setDriverClassName(dbDriver);
    47	        config.setJdbcUrl(dbUrl);
    48	        config.setUsername(dbUsername);
    49	        config.setPassword(dbPassword);
    50	        return new HikariDataSource(config);
    51	    }
    52	
    53	    @Bean
    54	    public BCryptPasswordEncoder passwordEncoder() {
    55	//        logger.info("Creating BCryptPasswordEncoder bean");
    56	        return new BCryptPasswordEncoder();
    57	    }
    58	
    59	    @Bean
    60	    public LocalContainerEntityManagerFactoryBean entityManagerFactory(DataSource dataSource) {
    61	        LocalContainerEntityManagerFactoryBean emf = new LocalContainerEntityManagerFactoryBean();
    62	        emf.setDataSource(dataSource);
    63	        emf.setPackagesToScan("fr._42.cinema.models");
    64	        emf.setJpaVendorAdapter(new HibernateJpaVendorAdapter());
    65	
    66	        emf.setEntityManagerFactoryInterface(jakarta.persistence.EntityManagerFactory.class);
    67	
    68	        Properties jpaProperties = new Properties();
    69	        jpaProperties.put("hibernate.dialect", "org.hibernate.dialect.PostgreSQLDialect");
    70	        jpaProperties.put("hibernate.hbm2ddl.auto", "update");
    71	        jpaProperties.put("hibernate.show_sql", "true");
    72	        emf.setJpaProperties(jpaProperties);
    73	
    74	        return emf;
    75	    }
    76	
    77	    @Bean
    78	    public PlatformTransactionManager transactionManager(EntityManagerFactory emf) {
    79	        return new JpaTransactionManager(emf);
    80	    }
    81	
    82	    @Bean
    83	    public ObjectMapper objectMapper() {
    84	        ObjectMapper mapper = new ObjectMapper();
    85	        mapper.registerModule(new JavaTimeModule());
    86	        mapper.disable(SerializationFeature.WRITE_DATES_AS_TIMESTAMPS);
    87	        return mapper;
    88	    }
