    41	            // Return the signup view so the error can be displayed
    42	            return "error"; // This should resolve to your signup.ftl
    43	        }
    44	        User user = new User(null, firstName, lastName, email, password, phoneNumber);
    45	        try {
    46	            // Attempt to sign up the user
    47	            usersService.signUp(user);
    48	//            return "redirect:/signin";
    49	            return "done";
    50	        } catch (Exception e) {
    51	            // Optionally, log the exception here
