     1	package fr._42.cinema.controller;
     2	
     3	import fr._42.cinema.models.User;
     4	import fr._42.cinema.services.UsersService;
     5	import jakarta.servlet.http.HttpSession;
     6	import org.springframework.beans.factory.annotation.Autowired;
     7	import org.springframework.stereotype.Controller;
     8	import org.springframework.ui.Model;
     9	import org.springframework.web.bind.annotation.*;
    10	
    11	@Controller
    12	@RequestMapping("/admin/signin")
    13	public class SigninController {
    14	    private UsersService usersService;
    15	
    16	    @Autowired
    17	    public SigninController(UsersService usersService) {
    18	        this.usersService = usersService;
    19	    }
    20	
    21	    @GetMapping(value = {"", "/"})
    22	    public String getSignIn() {
    23	        return "signIn";
    24	    }
    25	
    26	    @PostMapping(value = {"", "/"})
    27	    @ResponseBody
    28	    public String signIn(
    29	            @RequestParam("email") String email,
    30	            @RequestParam("password") String password,
    31	            Model model,
    32	            HttpSession session) {
    33	
    34	        if (email == null || email.trim().isEmpty() ||
    35	                password == null || password.trim().isEmpty()) {
    36	            model.addAttribute("error", "Please fill in all required fields.");
    37	            return "error";
    38	        }
    39	
    40	        try {
    41	            User user = usersService.singIn(email, password);
    42	            session.setAttribute("user", user);
    43	            session.setMaxInactiveInterval(60 * 20);
    44	            return "SignIn Successfully";
    45	        } catch (IllegalArgumentException e) {
    46	            model.addAttribute("error", e.getMessage());
    47	            return "error";
    48	        }
    49	    }
    50	}
