     1	drop table if exists users cascade;
     2	create table if not exists users(
     3	    id serial primary key,
     4	    first_name varchar(255),
     5	    last_name varchar(255),
     6	    phone_number varchar(255),
     7	    email varchar(255),
     8	    password varchar(255)
     9	);
    10	
    11	drop table if exists authentication_logs cascade;
    12	create table if not exists authentication_logs(
    13	    id serial primary key,
    14	    user_id integer,
    15	    login_time timestamp default current_timestamp,
    16	    ip_addr varchar(255)
    17	);
    18	
