    32	            Path file = Paths.get(imageDir).resolve(filename);
    33	            byte[] image = Files.readAllBytes(file);
    34	
    35	            // Determine MediaType dynamically
    36	            String mimeType = Files.probeContentType(file);
    37	            MediaType mediaType = mimeType != null ? MediaType.parseMediaType(mimeType) : MediaType.APPLICATION_OCTET_STREAM;
    38	            logger.info("Detected MediaType: " + mediaType); // Add logging
    39	
    40	            HttpHeaders headers = new HttpHeaders();
    41	            headers.setContentType(mediaType);
