     1	package fr._42.cinema.config;
     2	
     3	import org.slf4j.Logger;
     4	import org.slf4j.LoggerFactory;
     5	import org.springframework.context.annotation.Bean;
     6	import org.springframework.context.annotation.ComponentScan;
     7	import org.springframework.context.annotation.Configuration;
     8	import org.springframework.web.multipart.MultipartResolver;
     9	import org.springframework.web.multipart.support.StandardServletMultipartResolver;
    10	import org.springframework.web.servlet.ViewResolver;
    11	import org.springframework.web.servlet.config.annotation.AsyncSupportConfigurer;
    12	import org.springframework.web.servlet.config.annotation.DefaultServletHandlerConfigurer;
    13	import org.springframework.web.servlet.config.annotation.EnableWebMvc;
    14	import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;
    15	import org.springframework.web.servlet.view.freemarker.FreeMarkerConfigurer;
    16	import org.springframework.web.servlet.view.freemarker.FreeMarkerViewResolver;
    17	
    18	@Configuration
    19	@EnableWebMvc  // Enable Spring MVC
    20	@ComponentScan(basePackages = "fr._42.cinema.controller")  // Scan controllers
    21	public class WebConfig implements WebMvcConfigurer {
    22	    private static final Logger logger = LoggerFactory.getLogger(WebConfig.class);
    23	
    24	    @Bean
    25	    public ViewResolver viewResolver() {
    26	        FreeMarkerViewResolver resolver = new FreeMarkerViewResolver();
    27	        resolver.setCache(true);
    28	        resolver.setPrefix("");
    29	        resolver.setSuffix(".ftl");
    30	        resolver.setExposeRequestAttributes(true); // Expose HttpServletRequest attributes to the model
    31	        resolver.setExposeSessionAttributes(true);  // Optionally, expose HttpSession attributes too
    32	        // Optionally, set a name for the request context variable (default is "springMacroRequestContext")
    33	        resolver.setRequestContextAttribute("request");
    34	
    35	        return resolver;
    36	    }
    37	
    38	    @Bean
    39	    public FreeMarkerConfigurer freeMarkerConfigurer() {
    40	        FreeMarkerConfigurer configurer = new FreeMarkerConfigurer();
    41	        configurer.setTemplateLoaderPath("/WEB-INF/JSP/");
    42	        return configurer;
    43	    }
    44	
    45	    @Bean
    46	    public MultipartResolver multipartResolver() {
    47	        return new StandardServletMultipartResolver();
    48	    }
    49	
    50	    @Override
    51	    public void configureDefaultServletHandling(DefaultServletHandlerConfigurer configurer) {
    52	        // Enable default servlet handler for static resources
    53	        configurer.enable();
    54	        logger.info("Default servlet handling enabled");
    55	    }
    56	
    57	    @Override
    58	    public void configureAsyncSupport(AsyncSupportConfigurer configurer) {
    59	        // Configure async support for WebSocket
    60	        configurer.setDefaultTimeout(30000); // 30 seconds
    61	        logger.info("Async support configured with timeout: 30000ms");
    62	    }
