     1	-- Drop tables in correct order (child tables first due to foreign key constraints)
     2	drop table if exists sessions cascade;
     3	drop table if exists authentication_logs cascade;
     4	drop table if exists images_info cascade;
     5	drop table if exists users cascade;
     6	drop table if exists films cascade;
     7	drop table if exists halls cascade;
     8	
     9	-- Create users table
    10	create table if not exists users(
    11	    id bigserial primary key,
    12	    first_name varchar(255) not null,
    13	    last_name varchar(255) not null,
    14	    phone_number varchar(255) not null,
    15	    email varchar(255) not null unique,
    16	    password varchar(255) not null,
    17	    role varchar(50) not null default 'ADMIN'
    18	);
...
    45	
    46	-- Create sessions table
    47	create table if not exists sessions(
    48	    id bigserial primary key,
    49	    ticket_cost numeric(10,2) not null,
    50	    session_time timestamp not null,
    51	    film_id bigint not null,
    52	    hall_id bigint not null,
    53	    constraint fk_sessions_film foreign key (film_id) references films(id) on delete cascade,
    54	    constraint fk_sessions_hall foreign key (hall_id) references halls(id) on delete cascade
    55	);
    56	
    57	-- Create images_info table (keeping existing table as it might be used elsewhere)
    58	create table if not exists images_info(
    59	    id bigserial primary key,
    60	    user_id bigint,
    61	    image_name varchar(255),
    62	    image_size integer,
    63	    mime_type varchar(255),
    64	    constraint fk_images_user foreign key (user_id) references users(id) on delete cascade
