    @Override
    public void addFilm(Film film) {
        filmsRepository.save(film);
    }

    @Override
    public List<Film> getFilms() {
        return filmsRepository.findAll();
    }

    @Override
    public Film getFilmById(Long id) {
        return filmsRepository.findById(id).orElse(null);
    }
}
