   196	                // This might be a JSON string that needs to be unquoted
   197	                processedMessage = message.substring(1, message.length() - 1);
   198	                logger.info("Unquoted message: {}", processedMessage);
   199	            }
   200	
   201	            // Echo the message back to the test topic
   202	            String echoMessage = "Echo: " + processedMessage;
   203	            logger.info("Returning echo message for broadcasting: {}", echoMessage);
   204	
