    45	            entry.textContent = `${new Date().toLocaleTimeString()}: ${message}`;
    46	            log.appendChild(entry);
    47	            log.scrollTop = log.scrollHeight;
    48	        }
    49	
    50	        function setConnected(connected) {
    51	            connectButton.disabled = connected;
    52	            disconnectButton.disabled = !connected;
    53	            sendButton.disabled = !connected;
    54	            messageInput.disabled = !connected;
    55	
    56	            if (connected) {
    57	                addLogEntry('Connected to WebSocket', 'success');
    58	            } else {
    59	                addLogEntry('Disconnected from WebSocket', 'info');
    60	            }
    61	        }
    62	
    63	        function connect() {
    64	            addLogEntry('Attempting to connect...');
    65	
    66	            // Log SockJS availability
    67	            if (typeof SockJS === 'undefined') {
    68	                addLogEntry('SockJS is not defined!', 'error');
    69	                return;
    70	            }
    71	            addLogEntry('SockJS is available');
    72	
    73	            try {
    74	                // Create SockJS instance
    75	                addLogEntry('Creating SockJS connection to /ex00_war/ws');
    76	                const socket = new SockJS('/ex00_war/ws', null, {
    77	                    transports: ['websocket', 'xhr-streaming', 'xhr-polling'],
    78	                    debug: true
    79	                });
    80	
    81	                // Log Stomp availability
    82	                if (typeof Stomp === 'undefined') {
    83	                    addLogEntry('Stomp is not defined!', 'error');
    84	                    return;
    85	                }
    86	                addLogEntry('Stomp is available');
    87	
    88	                // Create Stomp client
    89	                stompClient = Stomp.over(socket);
    90	
    91	                // Enable debug logging
    92	                stompClient.debug = function(str) {
    93	                    console.log(str);
    94	                    // Only log important messages to avoid cluttering the UI
    95	                    if (str.includes('error') || str.includes('failed') || str.includes('connect')) {
    96	                        addLogEntry(`STOMP: ${str}`, str.includes('error') ? 'error' : 'info');
    97	                    }
    98	                };
    99	
   100	                // Connect to the broker
   101	                addLogEntry('Connecting to STOMP broker...');
   102	                stompClient.connect(
   103	                    {},
   104	                    frame => {
   105	                        setConnected(true);
   106	                        addLogEntry(`Connected: ${frame}`);
   107	
   108	                        // Subscribe to the greeting topic
   109	                        stompClient.subscribe('/topic/greetings', message => {
   110	                            addLogEntry(`Received: ${message.body}`, 'success');
   111	                        });
   112	                    },
   113	                    error => {
   114	                        addLogEntry(`Connection error: ${error}`, 'error');
   115	                        disconnect();
   116	                    }
   117	                );
   118	            } catch (e) {
   119	                addLogEntry(`Exception: ${e.message}`, 'error');
   120	                console.error(e);
   121	            }
   122	        }
   123	
   124	        function disconnect() {
   125	            if (stompClient !== null) {
   126	                try {
   127	                    stompClient.disconnect();
   128	                } catch (e) {
   129	                    addLogEntry(`Disconnect error: ${e.message}`, 'error');
   130	                }
   131	            }
   132	            setConnected(false);
   133	            stompClient = null;
   134	        }
   135	
   136	        function sendMessage() {
   137	            const message = messageInput.value;
   138	            if (!message.trim()) {
   139	                addLogEntry('Please enter a message', 'error');
   140	                return;
   141	            }
   142	
   143	            addLogEntry(`Sending: ${message}`);
   144	            try {
   145	                stompClient.send("/app/hello", {}, message);
   146	                messageInput.value = '';
   147	            } catch (e) {
   148	                addLogEntry(`Send error: ${e.message}`, 'error');
   149	            }
   150	        }
   151	
   152	        // Event listeners
   153	        connectButton.addEventListener('click', connect);
   154	        disconnectButton.addEventListener('click', disconnect);
   155	        sendButton.addEventListener('click', sendMessage);
   156	        messageInput.addEventListener('keypress', e => {
   157	            if (e.key === 'Enter') {
   158	                sendMessage();
   159	            }
   160	        });
   161	
   162	        // Log page load
   163	        addLogEntry('Page loaded. Click "Connect" to start WebSocket connection.');
