    @Override
    public void onStartup(ServletContext servletContext) throws ServletException {
        // Root context (AppConfig - JPA, services)
        AnnotationConfigWebApplicationContext rootContext = new AnnotationConfigWebApplicationContext();
        rootContext.register(AppConfig.class, WebSocketConfig.class);
        servletContext.addListener(new ContextLoaderListener(rootContext));

        // Web context (WebConfig - controllers, view resolvers)
        AnnotationConfigWebApplicationContext webContext = new AnnotationConfigWebApplicationContext();
        webContext.register(WebConfig.class);

        // DispatcherServlet for web components
        DispatcherServlet dispatcherServlet = new DispatcherServlet(webContext);
        var dispatcher = servletContext.addServlet("dispatcher", dispatcherServlet);
        dispatcher.setLoadOnStartup(1);
        dispatcher.addMapping("/");
        dispatcher.setMultipartConfig(new MultipartConfigElement("C:\\Users\\hamza\\AppData\\Local\\Temp", 2097152, 4194304, 0));
    }
}
