    <script>
        var stompClient = null;

        function connect() {
            var socket = new SockJS('/ex00_war/ws');
            stompClient = Stomp.over(socket);
            stompClient.connect({}, function(frame) {
                log("Connected: " + frame);

                stompClient.subscribe('/topic/films/1/chat/messages', function(message) {
                    try {
                        var chatMessage = JSON.parse(message.body);
                        log("Received chat: " + chatMessage.sender + ": " + chatMessage.content);
                    } catch (e) {
                        log("Received raw: " + message.body);
                    }
                });

                stompClient.subscribe('/topic/test', function(message) {
                    log("Received test: " + message.body);
                });
            });
        }

        function disconnect() {
            if (stompClient !== null) {
                stompClient.disconnect();
            }
            log("Disconnected");
        }

        function sendDirectMessage() {
            var message = document.getElementById('message').value || "Direct test message";
            var chatMessage = {
                sender: "direct-test-user",
                content: message
            };

            log("Sending direct message: " + JSON.stringify(chatMessage));
            stompClient.send("/app/films/1/chat/direct", {'content-type': 'application/json'}, JSON.stringify(chatMessage));
        }

        function testBroadcast() {
            log("Testing broadcast endpoint...");
            fetch('/ex00_war/broadcast-test')
                .then(response => response.text())
                .then(data => {
                    log("Broadcast response: " + data);
                })
                .catch(error => {
                    log("Broadcast error: " + error);
                });
        }

        function log(message) {
            var logDiv = document.getElementById('log');
            var p = document.createElement('p');
            p.appendChild(document.createTextNode(message));
            logDiv.appendChild(p);
            logDiv.scrollTop = logDiv.scrollHeight;
        }
    </script>
