   218	        function sendMessage() {
   219	            try {
   220	                if (!stompClient || !stompClient.connected) {
   221	                    addMessage('System', 'Not connected to chat server. Please connect first.', 'system');
   222	                    return;
   223	                }
   224	
   225	                // Get the sender name and message content
   226	                const senderInput = document.getElementById('sender');
   227	                const messageInput = document.getElementById('message');
   228	
   229	                username = senderInput.value.trim() || 'Anonymous';
   230	                const content = messageInput.value.trim();
   231	
   232	                if (!content) {
   233	                    return; // Don't send empty messages
   234	                }
   235	
   236	                // Create a message that matches the ChatMessage class structure
   237	                const chatMessage = {
   238	                    sender: username,
   239	                    content: content
   240	                };
   241	
   242	                console.log("Sending chat message:", chatMessage);
   243	
   244	                // Send the message with proper headers
   245	                stompClient.send(
   246	                    `/app/films/${filmId}/chat/send`,
   247	                    {
   248	                        'content-type': 'application/json'
   249	                    },
   250	                    JSON.stringify(chatMessage)
   251	                );
   252	
   253	                // Clear the message input
   254	                messageInput.value = '';
   255	                messageInput.focus();
   256	
   257	            } catch (e) {
   258	                console.error("Message send error:", e);
   259	                addMessage('System', 'Error sending message: ' + e.message, 'system');
   260	            }
   261	        }
   262	
   263	        // Add event listener for Enter key in message input
   264	        document.addEventListener('DOMContentLoaded', function() {
