    @GetMapping("/direct-broadcast")
    @ResponseBody
    public String directBroadcast() {
        logger.info("Direct broadcast endpoint called");

        try {
            // Send to test topic
            messagingTemplate.convertAndSend("/topic/test", "Direct broadcast message");
            logger.info("Sent message to /topic/test");

            // Send to films topic
            ChatMessage chatMessage = new ChatMessage("system", "Direct broadcast message");
            messagingTemplate.convertAndSend("/topic/films/1/chat/messages", chatMessage);
            logger.info("Sent message to /topic/films/1/chat/messages");

            return "Messages broadcast successfully. Check your WebSocket client.";
        } catch (Exception e) {
            logger.error("Error broadcasting messages: {}", e.getMessage(), e);
            return "Error: " + e.getMessage();
        }
    }
