    18	/**
    19	 * Application initializer that sets up the Spring application context
    20	 * when deployed to a servlet container like Tomcat.
    21	 */
    22	public class WebInitializer implements WebApplicationInitializer {
    23	    private static final Logger logger = LoggerFactory.getLogger(WebInitializer.class);
    24	
    25	    @Override
    26	    public void onStartup(ServletContext servletContext) throws ServletException {
    27	        logger.info("Starting web application initialization");
    28	
    29	        // Root context (AppConfig - JPA, services)
    30	        AnnotationConfigWebApplicationContext rootContext = new AnnotationConfigWebApplicationContext();
    31	        rootContext.register(AppConfig.class);
    32	        servletContext.addListener(new ContextLoaderListener(rootContext));
    33	        logger.info("Root context initialized with AppConfig");
    34	        String webInfPath = servletContext.getRealPath("/WEB-INF");
    35	
    36	        // Set a system property to store the WEB-INF path
    37	        System.setProperty("webinf.path", webInfPath);
    38	
    39	        // Register configuration classes
    40	        rootContext.register(AppConfig.class);
    41	
    42	        // Load properties from WEB-INF
    43	        try {
    44	            ResourcePropertySource propertySource = new ResourcePropertySource(
    45	                    "file:" + webInfPath + "/application.properties");
    46	            rootContext.getEnvironment().getPropertySources().addFirst(propertySource);
    47	            logger.info("Application properties loaded from WEB-INF");
    48	        } catch (IOException e) {
    49	            // Log error
    50	            System.err.println("Could not load properties: " + e.getMessage());
    51	            logger.error("Could not load properties: " + e.getMessage());
    52	        }       // Web context (WebConfig - controllers, view resolvers, WebSocket)
    53	
    54	        AnnotationConfigWebApplicationContext webContext = new AnnotationConfigWebApplicationContext();
    55	        webContext.register(WebConfig.class);
    56	        logger.info("Web context initialized with WebConfig and WebSocketConfig");
    57	
    58	        // DispatcherServlet for web components
    59	        DispatcherServlet dispatcherServlet = new DispatcherServlet(webContext);
    60	        ServletRegistration.Dynamic dispatcher = servletContext.addServlet("dispatcher", dispatcherServlet);
    61	        dispatcher.setLoadOnStartup(1);
    62	        dispatcher.addMapping("/");
    63	
    64	        // Configure multipart support for file uploads
    65	        dispatcher.setMultipartConfig(new MultipartConfigElement("\\\\wsl.localhost\\Ubuntu\\tmp", 2097152, 4194304, 0));
    66	
    67	
    68	        // Enable async support for WebSocket
    69	        dispatcher.setAsyncSupported(true);
    70	
    71	        logger.info("Web application initialization completed");
    72	    }
