    35	        var ctx = "${ctx}";
    36	
    37	        $(document).ready(function() {
    38	            $("#searchInput").on("keyup", function() {
    39	                var filmName = $(this).val().trim();
    40	                if (filmName.length > 1) {  // Trigger search if more than one character is entered
    41	                    $.ajax({
    42	                        url: ctx + "/sessions/search",
    43	                        method: "GET",
    44	                        data: { filmName: filmName },
    45	                        dataType: "json",
    46	                        success: function(data) {
    47	                            $("#resultsList").empty();
    48	                            if (data.sessions && data.sessions.length > 0) {
    49	                                $.each(data.sessions, function(index, session) {
    50	                                    var colDiv = $("<div class='col-md-3 col-sm-6 result-card'></div>");
    51	                                    if (session.film && session.film.posterUrl) {
    52	                                        colDiv.append("<img src='" + ctx + "/images/" + session.film.posterUrl + "' alt='Poster'>");
    53	                                    } else {
    54	                                        colDiv.append("<img src='https://via.placeholder.com/120x120?text=No+Poster' alt='Poster'>");
    55	                                    }
    56	                                    colDiv.append("<p><strong>Title:</strong> " + session.film.title+ "</p>");
    57	                                    // Format the session time from ISO format to readable format
    58	                                    var sessionTime = session.sessionTime;
    59	                                    if (sessionTime) {
    60	                                        // Convert "2025-06-27T17:27" to "Jun 27, 2025 at 17:27"
    61	                                        var date = new Date(sessionTime);
    62	                                        var formattedDate = date.toLocaleDateString('en-US', {
    63	                                            year: 'numeric',
    64	                                            month: 'short',
    65	                                            day: 'numeric'
    66	                                        }) + ' at ' + date.toLocaleTimeString('en-US', {
    67	                                            hour: '2-digit',
    68	                                            minute: '2-digit',
    69	                                            hour12: false
    70	                                        });
    71	                                        colDiv.append("<p><strong>Date & Time:</strong> " + formattedDate + "</p>");
    72	                                    } else {
    73	                                        colDiv.append("<p><strong>Date & Time:</strong> Not available</p>");
    74	                                    }
    75	                                    colDiv.append("<p><strong>Ticket Cost:</strong> " + session.ticketCost + "</p>");
    76	                                    // colDiv.append("<a href='" + ctx + "/sessions/" + session.id + "'>View Session</a>");
    77	                                    $("#resultsList").append(colDiv);
    78	                                });
    79	                            } else {
    80	                                $("#resultsList").append("<p class='col-12 text-center'>No matching sessions found.</p>");
    81	                            }
    82	                        },
    83	                        error: function(xhr, status, error) {
    84	                            console.error("AJAX error:", status, error);
    85	                            $("#resultsList").html("<p class='col-12 text-center text-danger'>Error retrieving data.</p>");
    86	                        }
    87	                    });
    88	                } else {
    89	                    $("#resultsList").empty();
    90	                }
    91	            });
    92	        });
