    24	@Controller
    25	@RequestMapping("/admin/panel/sessions")
    26	public class SessionsController {
    27	    private final SessionsService sessionsService;
    28	    private final HallsService hallsService;
    29	    private final FilmsService filmsService;
    30	    private final Logger logger = LoggerFactory.getLogger(SessionsController.class);
    31	
    32	    @Autowired
    33	    public SessionsController(
    34	            SessionsService sessionsService,
    35	            HallsService hallsService,
    36	            FilmsService filmsService
    37	    ) {
    38	        this.filmsService = filmsService;
    39	        this.hallsService = hallsService;
    40	        this.sessionsService = sessionsService;
    41	    }
    42	
    43	    @GetMapping(value = {"", "/"})
    44	    public String getSessions(Model model) {
    45	        try {
    46	            List<Film> films = filmsService.getFilms();
    47	            List<Session> sessions = sessionsService.getSessions();
    48	            List<Hall> halls = hallsService.getHalls();
    49	            model.addAttribute("films", films);
    50	            model.addAttribute("sessions", sessions);
    51	            model.addAttribute("halls", halls);
    52	        } catch (Exception e) {
    53	            model.addAttribute("error", "An error occurred while fetching data from the database");
    54	        } finally {
    55	            return "sessions";
    56	        }
    57	    }
    58	
    59	    @PostMapping(value = {"", "/"})
    60	    public String postSessions(
    61	            @RequestParam("filmId") Long filmId,
    62	            @RequestParam("hallId") Long hallId,
    63	            @RequestParam("sessionTime") @DateTimeFormat(pattern="yyyy-MM-dd'T'HH:mm") LocalDateTime sessionTime,
    64	            @RequestParam("ticketPrice") Double ticketPrice,
    65	            Model model
    66	    ) {
    67	        if (filmId == null || hallId == null || sessionTime == null || ticketPrice == null) {
    68	            model.addAttribute("error", "Please fill in all required fields.");
    69	            return "sessions";
    70	        }
    71	        Film film = filmsService.getFilmById(filmId);
    72	        Hall hall = hallsService.getHallById(hallId);
    73	
    74	        Session session = new Session(null, ticketPrice, sessionTime, film, hall);
    75	        try {
    76	            sessionsService.addSession(session);
    77	        } catch (Exception e) {
    78	            model.addAttribute("error", "An error occurred while saving the session into the database");
    79	        }
    80	        return "sessions";
    81	    }
    82	}
