     1	package fr._42.cinema.config;
     2	
     3	import org.springframework.context.annotation.Bean;
     4	import org.springframework.context.annotation.Configuration;
     5	import org.springframework.web.cors.CorsConfiguration;
     6	import org.springframework.web.cors.UrlBasedCorsConfigurationSource;
     7	import org.springframework.web.filter.CorsFilter;
     8	import org.slf4j.Logger;
     9	import org.slf4j.LoggerFactory;
    10	
    11	@Configuration
    12	public class CorsConfig {
    13	    private static final Logger logger = LoggerFactory.getLogger(CorsConfig.class);
    14	
    15	    @Bean
    16	    public CorsFilter corsFilter() {
    17	        logger.info("Configuring CORS filter");
    18	        
    19	        UrlBasedCorsConfigurationSource source = new UrlBasedCorsConfigurationSource();
    20	        CorsConfiguration config = new CorsConfiguration();
    21	        
    22	        // Allow all origins using patterns (more secure than using "*" with allowCredentials)
    23	        config.addAllowedOriginPattern("*");
    24	        
    25	        // Allow all HTTP methods
    26	        config.addAllowedMethod("*");
    27	        
    28	        // Allow all headers
    29	        config.addAllowedHeader("*");
    30	        
    31	        // Allow cookies and auth headers
    32	        config.setAllowCredentials(true);
    33	        
    34	        // How long the browser should cache the CORS response in seconds
    35	        config.setMaxAge(3600L);
    36	        
    37	        // Apply this configuration to all paths
    38	        source.registerCorsConfiguration("/**", config);
    39	        
    40	        logger.info("CORS filter configured successfully");
    41	        return new CorsFilter(source);
    42	    }
    43	}
