    42	
    43	    @GetMapping(value = {"", "/"})
    44	    public String getSessions(Model model) {
    45	        try {
    46	            List<Film> films = filmsService.getFilms();
    47	            List<Session> sessions = sessionsService.getSessions();
    48	            List<Hall> halls = hallsService.getHalls();
    49	            model.addAttribute("films", films);
    50	            model.addAttribute("sessions", sessions);
    51	            model.addAttribute("halls", halls);
    52	        } catch (Exception e) {
    53	            model.addAttribute("error", "An error occurred while fetching data from the database");
    54	        } finally {
    55	            return "sessions";
    56	        }
    57	    }
    58	
    59	    @PostMapping(value = {"", "/"})
    60	    public String postSessions(
    61	            @RequestParam("filmId") Long filmId,
    62	            @RequestParam("hallId") Long hallId,
    63	            @RequestParam("sessionTime") @DateTimeFormat(pattern="yyyy-MM-dd'T'HH:mm") LocalDateTime sessionTime,
    64	            @RequestParam("ticketPrice") Double ticketPrice,
    65	            Model model
    66	    ) {
    67	        if (filmId == null || hallId == null || sessionTime == null || ticketPrice == null) {
    68	            model.addAttribute("error", "Please fill in all required fields.");
    69	            return "sessions";
    70	        }
