     1	package fr._42.cinema.models;
     2	
     3	import jakarta.persistence.*;
     4	
     5	import java.time.LocalDateTime;
     6	import java.util.Objects;
     7	
     8	@Entity
     9	@Table(name = "sessions")
    10	public class Session {
    11	    @Id
    12	    @GeneratedValue(strategy = GenerationType.IDENTITY)
    13	    private Long id;
    14	
    15	    @Column(name = "ticket_cost", nullable = false)
    16	    private Double ticketCost;
    17	
    18	    @Column(name = "session_time", nullable = false)
    19	    private LocalDateTime sessionTime;
    20	
    21	    @ManyToOne
    22	    @JoinColumn(name = "film_id", nullable = false)
    23	    private Film film;
    24	
    25	    @ManyToOne
    26	    @JoinColumn(name = "hall_id", nullable = false)
    27	    private Hall hall;
    28	
    29	    public Session() {
    30	    }
    31	
    32	    public Session(Long id, Double ticketCost, LocalDateTime sessionTime, Film film, Hall hall) {
    33	        this.id = id;
    34	        this.ticketCost = ticketCost;
    35	        this.sessionTime = sessionTime;
    36	        this.film = film;
    37	        this.hall = hall;
    38	    }
    39	
    40	    @Override
    41	    public boolean equals(Object o) {
    42	        if (o == null || getClass() != o.getClass()) return false;
    43	        Session session = (Session) o;
    44	        return Objects.equals(id, session.id) && Objects.equals(ticketCost, session.ticketCost) && Objects.equals(sessionTime, session.sessionTime) && Objects.equals(film, session.film) && Objects.equals(hall, session.hall);
    45	    }
    46	
    47	    @Override
    48	    public int hashCode() {
    49	        return Objects.hash(id, ticketCost, sessionTime, film, hall);
    50	    }
    51	
    52	    @Override
    53	    public String toString() {
    54	        return "Session{" +
    55	                "id=" + id +
    56	                ", ticketCost=" + ticketCost +
    57	                ", sessionTime=" + sessionTime +
    58	                ", film=" + film +
    59	                ", hall=" + hall +
    60	                '}';
    61	    }
    62	
    63	    public Long getId() {
    64	        return id;
    65	    }
    66	
    67	    public void setId(Long id) {
    68	        this.id = id;
    69	    }
    70	
    71	    public Double getTicketCost() {
    72	        return ticketCost;
    73	    }
    74	
    75	    public void setTicketCost(Double ticketCost) {
    76	        this.ticketCost = ticketCost;
    77	    }
    78	
    79	    public LocalDateTime getSessionTime() {
    80	        return sessionTime;
    81	    }
    82	
    83	    public void setSessionTime(LocalDateTime sessionTime) {
    84	        this.sessionTime = sessionTime;
    85	    }
    86	
    87	    public Film getFilm() {
    88	        return film;
    89	    }
    90	
    91	    public void setFilm(Film film) {
    92	        this.film = film;
    93	    }
    94	
    95	    public Hall getHall() {
    96	        return hall;
    97	    }
    98	
    99	    public void setHall(Hall hall) {
   100	        this.hall = hall;
   101	    }
