    63	            @RequestParam("sessionTime") @DateTimeFormat(pattern="yyyy-MM-dd'T'HH:mm") LocalDateTime sessionTime,
    64	            @RequestParam("ticketPrice") Double ticketPrice,
    65	            Model model
    66	    ) {
    67	        // Always populate the model with required data for the template
    68	        try {
    69	            List<Film> films = filmsService.getFilms();
    70	            List<Session> sessions = sessionsService.getSessions();
    71	            List<Hall> halls = hallsService.getHalls();
    72	            model.addAttribute("films", films);
    73	            model.addAttribute("sessions", sessions);
    74	            model.addAttribute("halls", halls);
    75	        } catch (Exception e) {
    76	            model.addAttribute("error", "An error occurred while fetching data from the database");
    77	            return "sessions";
    78	        }
...
    85	
    86	        // Process the form submission
    87	        try {
    88	            Film film = filmsService.getFilmById(filmId);
    89	            Hall hall = hallsService.getHallById(hallId);
    90	
    91	            Session session = new Session(null, ticketPrice, sessionTime, film, hall);
    92	            sessionsService.addSession(session);
    93	
    94	            // Refresh the sessions list after adding new session
