     8	@Entity
     9	@Table(name = "sessions")
    10	public class Session {
    11	    @Id
    12	    @GeneratedValue(strategy = GenerationType.IDENTITY)
    13	    private Long id;
    14	
    15	    @Column(name = "ticket_cost", nullable = false)
    16	    private Double ticketCost;
    17	
    18	    @Column(name = "session_time", nullable = false)
    19	    private LocalDateTime sessionTime;
    20	
    21	    @ManyToOne
    22	    @JoinColumn(name = "film_id", nullable = false)
    23	    private Film film;
    24	
    25	    @ManyToOne
    26	    @JoinColumn(name = "hall_id", nullable = false)
    27	    private Hall hall;
    28	
    29	    public Session() {
    30	    }
    31	
    32	    public Session(Long id, Double ticketCost, LocalDateTime sessionTime, Film film, Hall hall) {
    33	        this.id = id;
    34	        this.ticketCost = ticketCost;
    35	        this.sessionTime = sessionTime;
    36	        this.film = film;
    37	        this.hall = hall;
